#include <cstdlib>#include <ctime>#include "CycledLetterSequence.h"char CycledLetterSequence::letters[] = {'A', 'B'};CycledLetterSequence::CycledLetterSequence() : seq() {	int size;		srand(time(0));	size = 1 + rand() % 30;	seq.resize(size);	for (int i = 0; i < size; i++)	{		seq[i] = (rand() % 2 == 0) ? 'A' : 'B';	}}CycledLetterSequence::CycledLetterSequence(std::string str) : seq(str) {}char CycledLetterSequence::charAtIndex(int index) {	int trueIndex = index % seq.length();	return seq[trueIndex];}std::string CycledLetterSequence::toString() {	return seq;}